import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { getSchedule, updateSchedule } from '../../api/scheduleApi';
import DriveFolderUploadOutlinedIcon from '@mui/icons-material/DriveFolderUploadOutlined';
import './ScheduleDetail.css';

const ScheduleDetail = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        name: "",
        origin: "",
        destination: "",
        price: "",
        departureDate: "",
        departureTime: "",
        arrivalTime: "",
        desc: "",
        totalSeats: "",
        amenities: "",
        stops: [],
        seatNumbers: [],
        photos: [] // Initialize photos array
    });
    const [file, setFile] = useState(null);
    const [error, setError] = useState(null);
    const [newStop, setNewStop] = useState({ stopName: "", arrivalTime: "" });

    useEffect(() => {
        const fetchSchedule = async () => {
            try {
                const { data } = await getSchedule(id);
                setFormData({
                    ...data,
                    departureDate: new Date(data.departureDate).toISOString().split('T')[0], // Format for input
                    amenities: data.amenities.join(', '), // Join array into string
                    photos: data.photos || [] // Ensure photos is an array
                });
            } catch (err) {
                setError('Failed to load schedule details');
            }
        };
        fetchSchedule();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => {
            if (name === "totalSeats") {
                const seatCount = parseInt(value) || 0;
                const seatNumbers = Array.from({ length: seatCount }, (_, index) => ({
                    number: index + 1,
                    isBooked: false
                }));
                return { ...prev, totalSeats: value, seatNumbers };
            }
            return { ...prev, [name]: value };
        });
    };

    const handleStopChange = (e) => {
        const { name, value } = e.target;
        setNewStop((prev) => ({ ...prev, [name]: value }));
    };

    const addStop = () => {
        if (newStop.stopName && newStop.arrivalTime) {
            setFormData((prev) => ({
                ...prev,
                stops: [...prev.stops, newStop]
            }));
            setNewStop({ stopName: "", arrivalTime: "" }); // Reset stop fields
        }
    };

    const removeStop = (index) => {
        setFormData((prev) => ({
            ...prev,
            stops: prev.stops.filter((_, i) => i !== index)
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);

        let imgUrl = formData.photos[0] || "";
        if (file) {
            const data = new FormData();
            data.append('file', file);
            data.append('upload_preset', 'upload');

            try {
                const uploadRes = await axios.post(
                    'https://api.cloudinary.com/v1_1/dhvb035xa/image/upload',
                    data
                );
                imgUrl = uploadRes.data.url;
            } catch (err) {
                setError('Failed to upload image');
                return;
            }
        }

        const updatedData = {
            ...formData,
            price: parseFloat(formData.price),
            totalSeats: parseInt(formData.totalSeats),
            photos: imgUrl ? [imgUrl] : [],
            amenities: formData.amenities.split(',').map(item => item.trim())
        };

        try {
            await updateSchedule(id, updatedData);
            alert('Schedule updated successfully');
            navigate(-1); // Navigate back
        } catch (err) {
            setError(err.response?.data?.message || 'Something went wrong');
        }
    };

    return (
        <div className="scheduleDetail">
            <h2>Update Schedule</h2>
            <form onSubmit={handleSubmit}>
                <div className="formGroup">
                    <label>Name:</label>
                    <input type="text" name="name" value={formData.name} onChange={handleChange} required />
                </div>
                <div className="formGroup">
                    <label>Origin:</label>
                    <input type="text" name="origin" value={formData.origin} onChange={handleChange} required />
                </div>
                <div className="formGroup">
                    <label>Destination:</label>
                    <input type="text" name="destination" value={formData.destination} onChange={handleChange} required />
                </div>
                <div className="formGroup">
                    <label>Price:</label>
                    <input type="number" name="price" value={formData.price} onChange={handleChange} required />
                </div>
                <div className="formGroup">
                    <label>Departure Date:</label>
                    <input type="date" name="departureDate" value={formData.departureDate} onChange={handleChange} required />
                </div>
                <div className="formGroup">
                    <label>Departure Time:</label>
                    <input type="time" name="departureTime" value={formData.departureTime} onChange={handleChange} required />
                </div>
                <div className="formGroup">
                    <label>Arrival Time:</label>
                    <input type="time" name="arrivalTime" value={formData.arrivalTime} onChange={handleChange} required />
                </div>
                <div className="formGroup">
                    <label>Description:</label>
                    <textarea name="desc" value={formData.desc} onChange={handleChange} required></textarea>
                </div>
                <div className="formGroup">
                    <label>Total Seats:</label>
                    <input type="number" name="totalSeats" value={formData.totalSeats} onChange={handleChange} required />
                </div>
                <div className="formGroup">
                    <label>Stops:</label>
                    <div className="stopInputs">
                        <input
                            type="text"
                            name="stopName"
                            value={newStop.stopName}
                            onChange={handleStopChange}
                            placeholder="Stop Name"
                        />
                        <input
                            type="time"
                            name="arrivalTime"
                            value={newStop.arrivalTime}
                            onChange={handleStopChange}
                            placeholder="Arrival Time"
                        />
                        <button type="button" onClick={addStop} className="addStopButton">Add Stop</button>
                    </div>
                    {formData.stops.length > 0 && (
                        <ul className="stopList">
                            {formData.stops.map((stop, index) => (
                                <li key={index}>
                                    {stop.stopName} - {stop.arrivalTime}
                                    <button type="button" onClick={() => removeStop(index)} className="removeStopButton">Remove</button>
                                </li>
                            ))}
                        </ul>
                    )}
                </div>
                <div className="formGroup">
                    <label>Amenities (comma-separated):</label>
                    <input type="text" name="amenities" value={formData.amenities} onChange={handleChange} />
                </div>
                <div className="formGroup">
                    <label>Photos:</label>
                    <div className="imageUploadSection">
                        <label htmlFor="file" className="imageUploadLabel">
                            <img
                                src={
                                    file
                                        ? URL.createObjectURL(file)
                                        : (formData.photos[0] || 'https://icons.veryicon.com/png/o/miscellaneous/common-fill-icon/gallery-33.png')
                                }
                                alt="Preview"
                                className="imagePreview"
                            />
                            <DriveFolderUploadOutlinedIcon className="uploadIcon" />
                        </label>
                        <input
                            type="file"
                            id="file"
                            onChange={(e) => setFile(e.target.files[0])}
                            style={{ display: 'none' }}
                            accept="image/*"
                        />
                    </div>
                </div>
                {error && <div className="errorMessage">{error}</div>}
                <div className="formGroup">
                    <button type="submit" className="submitButton">Update Schedule</button>
                </div>
            </form>
        </div>
    );
};

export default ScheduleDetail;
