import React, { useEffect, useState } from 'react';
import { getSchedules, createSchedule, updateSchedule, deleteSchedule } from '../../api/scheduleApi';
import AddScheduleForm from './AddScheduleForm';

const ScheduleManagement = () => {
    const [schedules, setSchedules] = useState([]);
    const [showAddForm, setShowAddForm] = useState(false);

    useEffect(() => {
        getSchedules().then(({ data }) => setSchedules(data));
    }, []);

    const handleAdd = () => {
        setShowAddForm(true);
    };

    const handleAddSchedule = (newSchedule) => {
        setSchedules([...schedules, newSchedule]);
    };

    const handleUpdate = (id) => {
        const name = prompt('Enter new schedule name:');
        if (name) updateSchedule(id, { name }).then(() => {
            setSchedules(schedules.map(schedule => schedule._id === id ? { ...schedule, name } : schedule));
        });
    };

    const handleDelete = (id) => {
        deleteSchedule(id).then(() => setSchedules(schedules.filter(schedule => schedule._id !== id)));
    };

    return (
        <div className="scheduleManagement">
            <h3>Schedule Management</h3>
            <button onClick={handleAdd}>Add Schedule</button>
            {showAddForm && (
                <AddScheduleForm
                    onClose={() => setShowAddForm(false)}
                    onAdd={handleAddSchedule}
                />
            )}
            <ul className="scheduleList">
                {schedules.map(schedule => (
                    <li key={schedule._id}>
                        <div className="scheduleItem">
                            <div className="scheduleInfo">
                                <h4>{schedule.name}</h4>
                                <p>{schedule.origin} - {schedule.destination}</p>
                                <p>Departure: {schedule.departureDate} at {schedule.departureTime}</p>
                                <p>Arrival: {schedule.arrivalTime}</p>
                                <p>Price: ${schedule.price}</p>
                                {schedule.photos && schedule.photos.length > 0 && (
                                    <img src={schedule.photos[0]} alt={schedule.name} className="schedulePhoto" />
                                )}
                            </div>
                            <div className="scheduleActions">
                                <button onClick={() => handleUpdate(schedule._id)}>Update</button>
                                <button onClick={() => handleDelete(schedule._id)}>Delete</button>
                            </div>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ScheduleManagement;
