import React, { useEffect, useState } from 'react';
import { getUsers, deleteUser, updateUser } from '../../api/userApi';

const UserManagement = () => {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        getUsers().then(({ data }) => setUsers(data));
    }, []);

    const handleDelete = (id) => {
        deleteUser(id).then(() => setUsers(users.filter(user => user._id !== id)));
    };

    const handleUpdate = (id) => {
        const username = prompt('Enter new username:');
        if (username) updateUser(id, { username }).then(() => {
            setUsers(users.map(user => user._id === id ? { ...user, username } : user));
        });
    };

    return (
        <div>
            <h3>User Management</h3>
            <ul>
                {users.map(user => (
                    <li key={user._id}>
                        {user.username} ({user.email})
                        <button onClick={() => handleUpdate(user._id)}>Update</button>
                        <button onClick={() => handleDelete(user._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default UserManagement;
