// src/components/AddScheduleForm.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { createSchedule } from '../../api/scheduleApi';
import DriveFolderUploadOutlinedIcon from "@mui/icons-material/DriveFolderUploadOutlined";
import './AddScheduleForm.css';


const AddScheduleForm = ({ onClose, onAdd }) => {
    const [formData, setFormData] = useState({
        name: "",
        origin: "",
        destination: "",
        price: "",
        departureDate: "",
        departureTime: "",
        arrivalTime: "",
        desc: "",
        totalSeats: "",
        amenities: "",
    });
    const [file, setFile] = useState(null);
    const [error, setError] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);

        let imgUrl = "";
        if (file) {
            const data = new FormData();
            data.append("file", file);
            data.append("upload_preset", "upload");

            try {
                const uploadRes = await axios.post(
                    "https://api.cloudinary.com/v1_1/dhvb035xa/image/upload",
                    data
                );
                imgUrl = uploadRes.data.url;
            } catch (err) {
                setError("Failed to upload image");
                return;
            }
        }

        const scheduleData = {
            ...formData,
            price: parseFloat(formData.price),
            totalSeats: parseInt(formData.totalSeats),
            photos: imgUrl ? [imgUrl] : [],
            amenities: formData.amenities.split(',').map(item => item.trim()),
        };

        try {
            const { data } = await createSchedule(scheduleData); // Use createSchedule here
            onAdd(data);
            onClose();
        } catch (err) {
            setError(err.response?.data?.message || "Something went wrong");
        }
    };

    return (
        <div className="scheduleManagement">
            <div className="header">
                <h3>Schedule Management</h3>
                <button className="addButton" onClick={handleAdd}>Add Schedule</button>
            </div>
            {showAddForm && (
                <AddScheduleForm
                    onClose={() => setShowAddForm(false)}
                    onAdd={handleAddSchedule}
                />
            )}
            <table className="managementTable">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Origin</th>
                        <th>Destination</th>
                        <th>Departure Date</th>
                        <th>Departure Time</th>
                        <th>Arrival Time</th>
                        <th>Price</th>
                        <th>Total Seats</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {schedules.map(schedule => (
                        <tr key={schedule._id}>
                            <td>{schedule.name}</td>
                            <td>{schedule.origin}</td>
                            <td>{schedule.destination}</td>
                            <td>{new Date(schedule.departureDate).toLocaleDateString()}</td>
                            <td>{schedule.departureTime}</td>
                            <td>{schedule.arrivalTime}</td>
                            <td>${schedule.price}</td>
                            <td>{schedule.totalSeats}</td>
                            <td>
                                <button onClick={() => handleUpdate(schedule._id)}>Update</button>
                                <button onClick={() => handleDelete(schedule._id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ScheduleManagement;
