import React, { useEffect, useState } from 'react';
import { getAllBookings, cancelBooking, deleteBooking } from '../../api/bookingApi';

const BookingManagement = () => {
    const [bookings, setBookings] = useState([]);

    useEffect(() => {
        getAllBookings().then(({ data }) => setBookings(data));
    }, []);

    const handleCancel = (bookingId) => {
        cancelBooking(bookingId).then(({ data }) => {
            setBookings(bookings.map(booking =>
                booking._id === bookingId ? { ...booking, status: 'cancelled' } : booking
            ));
            alert(data.message); // Show success message
        }).catch(error => {
            console.error("Error cancelling booking:", error);
            alert("Error cancelling booking. Please try again.");
        });
    };

    const handleDelete = (bookingId) => {
        if (window.confirm("Are you sure you want to delete this booking?")) {
            deleteBooking(bookingId).then(() => {
                setBookings(bookings.filter(booking => booking._id !== bookingId));
                alert("Booking deleted successfully.");
            }).catch(error => {
                console.error("Error deleting booking:", error);
                alert("Error deleting booking. Please try again.");
            });
        }
    };

    return (
        <div>
            <h3>Booking Management</h3>
            <ul>
                {bookings.map((booking) => (
                    <li key={booking._id}>
                        <p>Booking ID: {booking._id}, Status: {booking.status}</p>
                        <p>Schedule: {booking.schedule.name} ({booking.schedule.origin} - {booking.schedule.destination})</p>
                        <button
                            disabled={booking.status === 'cancelled' || booking.status === 'completed'}
                            onClick={() => handleCancel(booking._id)}
                        >
                            Cancel
                        </button>
                        <button onClick={() => handleDelete(booking._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default BookingManagement;
