export const userColumns = [
  { field: "id", headerName: "ID", width: 70 },
  {
    field: "user",
    headerName: "User",
    width: 230,
    renderCell: (params) => {
      return (
        <div className="cellWithImg">
          <img className="cellImg" src={params.row.img || "https://i.ibb.co/MBtjqXQ/no-avatar.gif"} alt="avatar" />
          {params.row.username}
        </div>
      );
    },
  },
  {
    field: "email",
    headerName: "Email",
    width: 750,
  },
];

export const scheduleColomns = [
  { field: "_id", headerName: "ID", width: 230 }, // Assuming each bus entry has a unique ID
  {
    field: "name",
    headerName: "Bus Name",
    width: 150,
  },
  {
    field: "origin",
    headerName: "Origin",
    width: 150,
  },
  {
    field: "destination",
    headerName: "Destination",
    width: 150,
  },
  {
    field: "price",
    headerName: "Price (RM)",
    width: 70,
  },
  {
    field: "departureDate",
    headerName: "Departure Date",
    width: 150,
  },
  {
    field: "departureTime",
    headerName: "Departure Time",
    width: 70,
  },
  {
    field: "totalSeats",
    headerName: "Total Seats",
    width: 130,
  },
  {
    field: "seatNumbers",
    headerName: "Seats",
    width: 300,
    renderCell: (params) => {
      // Render a simple seat booking status
      const seatInfo = params.row.seatNumbers
        .map(seat => `#${seat.number} (${seat.isBooked ? 'Booked' : 'Available'})`)
        .join(', ');
      return <div>{seatInfo}</div>;
    }
  },
  {
    field: "desc",
    headerName: "Description",
    width: 300,
  }
];
