import React, { useState, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import "./Login.css"; // Ensure this points to your common styles file

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const { login } = useContext(AuthContext);

  const handleLogin = (e) => {
    e.preventDefault();
    login({ username, password });
  };

  return (
    <div className="authPage"> {/* Using common 'authPage' class */}
      <div className="authForm"> {/* Using common 'authForm' class */}
        <h2>Login</h2>
        <form onSubmit={handleLogin}>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" className="authButton">Login</button> {/* Using common 'authButton' class */}
        </form>
      </div>
    </div>
  );
};

export default Login;
