import axios from 'axios';

const apiClient = axios.create({
    baseURL: 'http://localhost:5000/api/schedules', // Adjust if necessary
    withCredentials: true, // Include credentials if needed for authorization
});

export const getSchedules = () => axios.get('/api/schedules');
export const getSchedule = (id) => axios.get(`/api/schedules/${id}`);
export const createSchedule = (data) => axios.post('/api/schedules', data);
export const updateSchedule = (id, data) => axios.put(`/api/schedules/${id}`, data);
export const deleteSchedule = (id) => axios.delete(`/api/schedules/${id}`);
