import React, { useEffect, useState } from 'react';
import { getUserBookings, cancelBooking } from '../../api/bookingApi';

const BookingManagement = () => {
    const [bookings, setBookings] = useState([]);
    const userId = 'user_id_example';  // replace with actual user ID as needed

    useEffect(() => {
        getUserBookings(userId).then(({ data }) => setBookings(data));
    }, [userId]);

    const handleCancel = (bookingId) => {
        cancelBooking(bookingId).then(({ data }) => {
            setBookings(bookings.map(booking => 
                booking._id === bookingId ? { ...booking, status: 'cancelled' } : booking
            ));
        });
    };

    return (
        <div>
            <h3>Booking Management</h3>
            <ul>
                {bookings.map(booking => (
                    <li key={booking._id}>
                        Booking ID: {booking._id}, Status: {booking.status}
                        <button disabled={booking.status === 'cancelled'} onClick={() => handleCancel(booking._id)}>Cancel</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default BookingManagement;
