// src/pages/ScheduleDetail/ScheduleDetail.jsx
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getSchedule, updateSchedule } from '../../api/scheduleApi';
import './ScheduleDetail.css';

const ScheduleDetail = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [schedule, setSchedule] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [formData, setFormData] = useState({
        name: '',
        origin: '',
        destination: '',
        departureDate: '',
        departureTime: '',
        arrivalTime: '',
        price: 0,
        totalSeats: 0,
        desc: '',
        amenities: []
    });

    useEffect(() => {
        const fetchSchedule = async () => {
            try {
                const { data } = await getSchedule(id);
                setSchedule(data);
                setFormData({
                    ...data,
                    departureDate: new Date(data.departureDate).toISOString().split('T')[0] // Format date for input
                });
            } catch (err) {
                setError('Failed to fetch schedule details');
                console.error(err);
            } finally {
                setLoading(false);
            }
        };
        fetchSchedule();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await updateSchedule(id, formData);
            alert('Schedule updated successfully');
            navigate(-1); // Go back to the previous page
        } catch (err) {
            setError('Failed to update schedule');
            console.error(err);
        }
    };

    if (loading) return <div>Loading schedule details...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="scheduleDetail">
            <h2>Update Schedule</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Name:
                    <input
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Origin:
                    <input
                        type="text"
                        name="origin"
                        value={formData.origin}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Destination:
                    <input
                        type="text"
                        name="destination"
                        value={formData.destination}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Departure Date:
                    <input
                        type="date"
                        name="departureDate"
                        value={formData.departureDate}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Departure Time:
                    <input
                        type="text"
                        name="departureTime"
                        value={formData.departureTime}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Arrival Time:
                    <input
                        type="text"
                        name="arrivalTime"
                        value={formData.arrivalTime}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Price:
                    <input
                        type="number"
                        name="price"
                        value={formData.price}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Total Seats:
                    <input
                        type="number"
                        name="totalSeats"
                        value={formData.totalSeats}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Description:
                    <textarea
                        name="desc"
                        value={formData.desc}
                        onChange={handleChange}
                        required
                    ></textarea>
                </label>
                <button type="submit">Update Schedule</button>
                <button type="button" onClick={() => navigate(-1)}>Cancel</button>
            </form>
        </div>
    );
};

export default ScheduleDetail;
