import React, { useEffect, useState } from 'react';
import { getUsers, deleteUser, updateUser, getUser } from '../../api/userApi';
import './UserManagement.css';

const defaultProfilePic = "https://static.thenounproject.com/png/638636-200.png";

const UserManagement = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedUser, setSelectedUser] = useState(null);
    const [editMode, setEditMode] = useState(false);
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        img: '',
    });

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const { data } = await getUsers();
                setUsers(data);
            } catch (err) {
                setError('Failed to fetch users');
                console.error(err);
            } finally {
                setLoading(false);
            }
        };
        fetchUsers();
    }, []);

    const handleEditClick = async (id) => {
        try {
            const { data } = await getUser(id);
            setSelectedUser(data);
            setFormData({ username: data.username, email: data.email, img: data.img });
            setEditMode(true);
        } catch (err) {
            console.error('Failed to fetch user data:', err);
            setError('Failed to fetch user data');
        }
    };

    const handleFormChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleConfirmEdit = async () => {
        try {
            const { data } = await updateUser(selectedUser._id, formData);
            setUsers(users.map(user => user._id === selectedUser._id ? data : user));
            setEditMode(false);
            setSelectedUser(null);
        } catch (err) {
            console.error('Failed to update user:', err);
            setError('Failed to update user');
        }
    };

    const handleDelete = async (id) => {
        try {
            await deleteUser(id);
            setUsers(users.filter(user => user._id !== id));
        } catch (err) {
            console.error('Failed to delete user:', err);
            setError('Failed to delete user');
        }
    };

    if (loading) return <div>Loading users...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="userManagement">
            <h3>User Management</h3>
            {editMode ? (
                <div className="editForm">
                    <h4>Edit User</h4>
                    <label>Profile Picture:</label>
                    <img 
                        src={formData.img || defaultProfilePic} 
                        alt="Profile" 
                        className="editProfilePic" 
                    />
                    <input
                        type="text"
                        name="img"
                        placeholder="Enter image URL"
                        value={formData.img}
                        onChange={handleFormChange}
                    />
                    <label>Username:</label>
                    <input
                        type="text"
                        name="username"
                        value={formData.username}
                        onChange={handleFormChange}
                    />
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleFormChange}
                    />
                    <button onClick={handleConfirmEdit}>Confirm Edit</button>
                    <button onClick={() => setEditMode(false)}>Cancel</button>
                </div>
            ) : (
                <table className="managementTable">
                    <thead>
                        <tr>
                            <th>Profile</th>
                            <th>Username</th>
                            <th>Email</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user._id}>
                                <td>
                                    <img 
                                        src={user.img || defaultProfilePic} 
                                        alt="User Profile" 
                                        className="userTablePic" 
                                    />
                                </td>
                                <td>{user.username}</td>
                                <td>{user.email}</td>
                                <td>
                                    <button onClick={() => handleEditClick(user._id)}>Edit</button>
                                    <button onClick={() => handleDelete(user._id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default UserManagement;
